import { LitElement, css, html } from 'lit-element';

class DemoEl extends LitElement {
  static get properties() {
    return {
      age: { type: Number },
    };
  }

  static get styles() {
    return css`
      h1 {
        color: red;
      }
    `;
  }

  constructor() {
    super();
    this.age = 8;
  }

  render() {
    return html`
      <h1>I am ${this.age} years</h1>
      <button @click=${() => (this.age += 1)}>Celebrate</button>
    `;
  }
}

customElements.define('demo-el', DemoEl);
